func TestBorderTop(t *testing.T) {
	s := "something\nto border\nup"
	assert.Equal(t,
		"\x1b[38;5;23;48;5;255m─────────\x1b[0m\nsomething\nto border\nup       ",
		Border(NormalBorder(WithFgColor(23), WithBgColor(255)), s, false, true, true, true),
	)
}

