func TestTop(t *testing.T) {
	s := "margin\nis\ncool"
	assert.Equal(t,
		"      \n      \n      \n      \n      \nmargin\nis    \ncool  ",
		MakeSquare(Right, MarginTop(5, s)),
	)
}

